void setup()
{
  Serial.begin(9600);
  pinMode(ledPin, OUTPUT);
  Serial.println("Dallas Temperature IC Control Library");
  sensors.begin();
  display.begin(SSD1306_SWITCHCAPVCC, 0x3C);
  display.display();
  display.clearDisplay();
  display.setFont(&FreeSans9pt7b);
  display.setTextColor(WHITE);
}

int xx = 50;
int yy = 0;
int tt = 5;

float ldrToLumen(int ldrValue)
{
  float lumen = 0.0;
  lumen = map(ldrValue, 0, 1023, 0, 100);
  return lumen;
}

void loop()
{

  Serial.println("Requesting temperatures...");
  sensors.requestTemperatures();
  Serial.print("Temperature is: ");
  Serial.print(sensors.getTempCByIndex(0), 0);
  Serial.println(" *C");
  val = analogRead(analogPin);
  float lumen = ldrToLumen(val);
  Serial.print("Lumen = ");
  Serial.println(lumen);
  int sensorValue = analogRead(sensorPin);
  int moisture = map(sensorValue, 0, 4095, 0, 100);
  Serial.print("Moisture: ");
  Serial.print(moisture);
  Serial.println("%");

  display.clearDisplay();
  display.setTextSize(1);
  display.setCursor(0, 20);
  display.print("Temp : ");
  display.print(sensors.getTempCByIndex(0));
  display.display();
  delay(5000);

  display.clearDisplay();
  display.setTextSize(1);
  display.setCursor(0, 20);
  display.print("Lumen : ");
  display.print(val);
  display.print("  lm");
  display.display();
  delay(5000);

  display.clearDisplay();
  display.setTextSize(1);
  display.setCursor(0, 20);
  display.print("Mositure : ");
  display.print(moisture);
  display.print("  %");
  display.display();
  delay(5000);

  if (sensors.getTempCByIndex(0) > 28.00 || val < 400)
  {
    for (int i = 0; i < sick_frames_size; i++)
    {
      display.clearDisplay();
      display.drawBitmap(xx, yy, sick_frames[i], frame_size, frame_size, 1);
      display.display();
      delay(tt);
    }
  }
  else if (sensors.getTempCByIndex(0) < 28.00 || val > 400)
  {
    for (int i = 0; i < smile_frames_size; i++)
    {
      display.clearDisplay();
      display.drawBitmap(xx, yy, smile_frames[i], frame_size, frame_size, 1);
      display.display();
      delay(tt);
    }
  }
  else if (sensors.getTempCByIndex(0) < 28.00 || val < 400)
  {
    for (int i = 0; i < sad_frames_size; i++)
    {
      display.clearDisplay();
      display.drawBitmap(xx, yy, sad_frames[i], frame_size, frame_size, 1);
      display.display();
      delay(tt);
    }
  }
  else
  {
    for (int i = 0; i < netural_frames_size; i++)
    {
      display.clearDisplay();
      display.drawBitmap(xx, yy, netural_frames[i], frame_size, frame_size, 1);
      display.display();
      delay(tt);
    }
  }
}